con:config.script:
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import groovy.time.TimeCategory

// Biến đếm số lần chạy (gán vào Property testCase để lưu state giữa các lần chạy)
def runCountProperty = 'runCount'
def runCount = context.testCase.getPropertyValue(runCountProperty) as Integer ?: 0
runCount += 1
context.testCase.setPropertyValue(runCountProperty, runCount.toString())

log.info "Run count: ${runCount}"

// Lấy nội dung gốc của request
def originalRequest = context.expand('${post flights#Request}')

// Parse JSON
def requestData = new JsonSlurper().parseText(originalRequest)

// Base ngày hôm nay
def now = new Date()

use(TimeCategory) {
    def baseDeparture = now + runCount.days   // Mỗi lần chạy cộng thêm số ngày theo runCount
    baseDeparture.clearTime()                 // reset về 00:00:00

    // Set giờ cụ thể cho departure và arrival
    baseDeparture.setHours(8)
    baseDeparture.setMinutes(0)

    def baseArrival = baseDeparture + 3.hours  // arrival sau departure 3 tiếng

    // Format ISO
    def dateFormat = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm")
    requestData.departureTime = dateFormat.format(baseDeparture)
    requestData.arrivalTime = dateFormat.format(baseArrival)
}

// Update lại body của POST request
def updatedRequest = JsonOutput.prettyPrint(JsonOutput.toJson(requestData))
testRunner.testCase.testSteps["post flights"].setPropertyValue("Request", updatedRequest)

log.info "Updated Request:\n" + updatedRequest

con:configuration.scriptText:
import groovy.json.JsonSlurper

// Lấy dữ liệu từ test step "get flights"
def flightResponse = new JsonSlurper().parseText(context.expand('${get flights#Response}'))

// Lấy request body của POST từ test step hiện tại thông qua testRunner
def postData = new JsonSlurper().parseText(context.requestContent)

// Kiểm tra nếu flightNumber đã tồn tại
def existingFlight = flightResponse.find { it.flightNumber == postData.flightNumber }

assert !existingFlight : "Flight number ${postData.flightNumber} already exists."

// Nếu không có chuyến bay trùng, in ra thông báo thành công
println("Flight can be booked successfully!")

con:config.con:executeScript:
import com.eviware.soapui.support.GroovyUtils
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

// Lấy request gốc từ step "post bookings"
def request = testRunner.testCase.testSteps["post bookings"].getPropertyValue("Request")

// Parse JSON
def jsonSlurper = new JsonSlurper()
def originalData = jsonSlurper.parseText(request)

// Danh sách các field cần phá kiểu dữ liệu
def invalidFields = [
    ["path": ["email"], "invalidValue": 12345],               // email phải là String => truyền số
    ["path": ["phoneNumber"], "invalidValue": 987654321],      // phoneNumber phải là String => truyền số
    ["path": ["seatClass"], "invalidValue": 100],              // seatClass phải là String => truyền số
    ["path": ["returnSeatClass"], "invalidValue": 200],        // returnSeatClass phải là String hoặc null => truyền số
    ["path": ["price"], "invalidValue": "zero"],               // price phải là số => truyền String
    ["path": ["isRoundTrip"], "invalidValue": "falseString"],  // isRoundTrip phải là boolean => truyền String
    ["path": ["bookingStatus"], "invalidValue": 123]           // bookingStatus phải là String => truyền số
]

// Hàm để set giá trị vào JSON lồng nhau
def setNestedValue(Map obj, List path, value) {
    if (path.size() == 1) {
        obj[path[0]] = value
    } else {
        setNestedValue(obj[path[0]], path[1..-1], value)
    }
}

// Chạy từng trường 1
invalidFields.each { field ->
    // Clone lại dữ liệu gốc
    def tempData = jsonSlurper.parseText(JsonOutput.toJson(originalData))

    // Gán giá trị lỗi
    setNestedValue(tempData, field.path, field.invalidValue)

    // Convert lại thành JSON
    def modifiedRequest = JsonOutput.prettyPrint(JsonOutput.toJson(tempData))

    // Set lại request vào step
    testRunner.testCase.testSteps["post bookings"].setPropertyValue("Request", modifiedRequest)

    // Submit request (gửi request mới đã bị chỉnh lỗi)
    def response = testRunner.testCase.testSteps["post bookings"].run(testRunner, context)

    // Log kết quả
    log.info "Test field invalid type: ${field.path.join('.')}"
    log.info "HTTP Status: ${response.getPropertyValue('ResponseStatusCode')}"  // Nếu bạn capture status code

    // Nếu cần check status code phải 400
    def statusCode = response.getPropertyValue('ResponseStatusCode') as Integer
    if (statusCode != 400) {
        log.error "FAILED for field: ${field.path.join('.')}. Expected 400 but got ${statusCode}"
    }
}

// Sau khi test xong, reset lại request gốc (không để dữ liệu lỗi nữa)
testRunner.testCase.testSteps["post bookings"].setPropertyValue("Request", JsonOutput.prettyPrint(JsonOutput.toJson(originalData)))
log.info "Reset request về dữ liệu gốc xong."
