con:configuration.scriptText:
def responseXml = context.response

// Parse XML with correct namespace
def xml = new XmlSlurper(false, false).parseText(responseXml)
xml.declareNamespace(ns2: 'http://jonclarke.dev/movies/web-service')

// Get list of movie IDs from all <ns2:movies> elements
def ids = xml.'SOAP-ENV:Body'.'ns2:listMoviesResponse'.'ns2:movies'.collect {
    it.'ns2:id'.text().toInteger()
}

// Assert conditions
assert ids.size() == 2 : "Expected 2 movies, but got ${ids.size()}"
assert ids.containsAll([1, 2]) : "Expected IDs 1 and 2, but got ${ids}"
